import mongoose, { Schema } from 'mongoose';
const StudentSchema = new Schema({
    name: {
        type: String,
        required: true
    },
    age: {
        type: Number,
        required: true
    },
    email: {
        type: String,
        required: true,
        unique: true
    },
    course: {
        type: String,
        required: true
    },
}, { timestamps: true });
export const Student = mongoose.model('Student', StudentSchema);
